import Foundation

struct BadgeDefinition: Identifiable, Codable {
    let id: String
    let name: String
    let milestoneDays: Int
    let imageAssetName: String
    let rarityLevel: String
    let description: String
    let shareQuote: String

    static let defaultBadges: [BadgeDefinition] = [
        BadgeDefinition(id: "day0", name: "Lantern", milestoneDays: 0, imageAssetName: "badge_day0", rarityLevel: "starter", description: "Every journey begins with a single spark. This lantern lights your path through the darkness ahead.", shareQuote: "\"A journey of a thousand miles begins with a single step.\" — Lao Tzu"),
        BadgeDefinition(id: "day1", name: "Apprentice", milestoneDays: 1, imageAssetName: "badge_day1", rarityLevel: "common", description: "You've taken your first step on the path of mastery. The apprentice learns that strength comes from within.", shareQuote: "\"All we have to decide is what to do with the time that is given us.\" — Gandalf"),
        BadgeDefinition(id: "day3", name: "Compass", milestoneDays: 3, imageAssetName: "badge_day3", rarityLevel: "common", description: "Three days of clarity! This compass always points toward your true north, guiding you through temptation.", shareQuote: "\"It is not the strength of the body, but the strength of the spirit.\" — Tolkien"),
        BadgeDefinition(id: "day7", name: "Shield", milestoneDays: 7, imageAssetName: "badge_day7", rarityLevel: "uncommon", description: "A full week of resilience! This shield protects you from the arrows of doubt and craving.", shareQuote: "\"Even the smallest person can change the course of the future.\" — Galadriel"),
        BadgeDefinition(id: "day10", name: "Winglett", milestoneDays: 10, imageAssetName: "badge_day10", rarityLevel: "uncommon", description: "Ten days of new perspective! Winglett the baby hawk spreads curious wings, learning to soar above old patterns. You're rising higher with each passing day.", shareQuote: "\"The only way to discover the limits of the possible is to go beyond them into the impossible.\" — Arthur C. Clarke"),
        BadgeDefinition(id: "day14", name: "Campfire", milestoneDays: 14, imageAssetName: "badge_day14", rarityLevel: "uncommon", description: "Two weeks strong! Gather around this campfire - a place of warmth, rest, and renewed determination.", shareQuote: "\"Courage is not the absence of fear, but acting in spite of it.\" — Mark Twain"),
